use pacwrap_core::{
    utils::{arguments::Operand, is_truecolor_terminal, Arguments},
    Result,
};

use crate::help::version_string;

fn minimal(args: &mut Arguments) -> bool {
    args.into_iter()
        .filter(|a| a == &Operand::LongPos("version", "min") || a == &Operand::ShortPos('V', "min"))
        .last()
        .is_some()
}

